  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '对接 Okta (SAML)',
	description: '如果您的组织使用 Okta Identity Provider （IdP）进行用户身份验证，您可以配置 Rancher 以允许您的用户使用他们的 IdP 凭据登录。Okta 集成仅支持服务提供者发起的登录。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'系统管理员指南',
		'登录认证',
		'对接 Okta (SAML)'
	]
};
export const metadata = {
  "id": "rancher2/admin-settings/authentication/okta/_index",
  "title": "对接 Okta (SAML)",
  "description": "如果您的组织使用 Okta Identity Provider （IdP）进行用户身份验证，您可以配置 Rancher 以允许您的用户使用他们的 IdP 凭据登录。Okta 集成仅支持服务提供者发起的登录。",
  "source": "@site/docs/rancher2/admin-settings/authentication/okta/_index.md",
  "permalink": "/docs/rancher2/admin-settings/authentication/okta/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/admin-settings/authentication/okta/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "rancher2",
  "previous": {
    "title": "配置 Rancher 使用 AD FS",
    "permalink": "/docs/rancher2/admin-settings/authentication/microsoft-adfs/rancher-adfs-setup/_index"
  },
  "next": {
    "title": "配置指南",
    "permalink": "/docs/rancher2/admin-settings/authentication/shibboleth/_index"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '先决条件',
  id: '先决条件',
  children: []
}, {
  value: '在 Rancher 中配置 Okta',
  id: '在-rancher-中配置-okta',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><em parentName="p">{`v2.2.0 版本可用`}</em></p>
    <p>{`如果您的公司使用 Okta Identity Provider （IdP）进行用户身份验证，您可以在 Rancher 中配置使用 IdP 凭证登录。`}</p>
    <p><strong parentName="p">{`注：`}</strong>{` Okta 仅支持服务提供者发起的登录。`}</p>
    <h2 {...{
      "id": "先决条件"
    }}>{`先决条件`}</h2>
    <p>{`在 Okta 中，使用下面的设置创建一个 SAML 应用程序。参见`}<a parentName="p" {...{
        "href": "https://developer.okta.com/standards/SAML/setting_up_a_saml_application_in_okta"
      }}>{`Okta 文档`}</a>{`以获得帮助。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`设置`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`值`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`Single Sign on URL`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`https://yourRancherHostURL/v1-saml/okta/saml/acs`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`Audience URI （SP Entity ID）`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`https://yourRancherHostURL/v1-saml/okta/saml/metadata`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "在-rancher-中配置-okta"
    }}>{`在 Rancher 中配置 Okta`}</h2>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`在`}<strong parentName="p">{`全局`}</strong>{`视图中，从主菜单中选择`}<strong parentName="p">{`安全 > 认证`}</strong>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`选择`}<strong parentName="p">{`Okta`}</strong>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`完成`}<strong parentName="p">{`配置 Okta 帐户`}</strong>{`表单。下面的示例描述了如何将 Okta 属性从属性 Statement 映射到 Rancher 中的字段。`}</p>
        <table parentName="li">
          <thead parentName="table">
            <tr parentName="thead">
              <th parentName="tr" {...{
                "align": null
              }}>{`字段`}</th>
              <th parentName="tr" {...{
                "align": null
              }}>{`描述`}</th>
            </tr>
          </thead>
          <tbody parentName="table">
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": null
              }}>{`显示名称`}</td>
              <td parentName="tr" {...{
                "align": null
              }}>{`属性 Statement 中包含用户 display name 的属性名称。`}</td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": null
              }}>{`用户名`}</td>
              <td parentName="tr" {...{
                "align": null
              }}>{`属性 Statement 中包含用户 user name/given name 属性名称。`}</td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": null
              }}>{`UID`}</td>
              <td parentName="tr" {...{
                "align": null
              }}>{`属性 Statement 中每个用户唯一的属性名称。`}</td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": null
              }}>{`组`}</td>
              <td parentName="tr" {...{
                "align": null
              }}>{`组属性 Statement 中代表组信息的属性名称。`}</td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": null
              }}>{`Rancher API 地址`}</td>
              <td parentName="tr" {...{
                "align": null
              }}>{`Rancher Server 的 URL 地址`}</td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": null
              }}>{`私钥/证书`}</td>
              <td parentName="tr" {...{
                "align": null
              }}>{`密钥/证书对，用于声明加密。`}</td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": null
              }}>{`元数据 XML`}</td>
              <td parentName="tr" {...{
                "align": null
              }}>{`在应用程序`}<inlineCode parentName="td">{`登录`}</inlineCode>{`部分得到的`}<inlineCode parentName="td">{`Identity Provider metadata`}</inlineCode>{`文件。`}</td>
            </tr>
          </tbody>
        </table>
        <blockquote parentName="li">
          <p parentName="blockquote"><strong parentName="p">{`提示：`}</strong>{` 您可以使用 openssl 命令生成密钥/证书对。例如：`}</p>
          <pre parentName="blockquote"><code parentName="pre" {...{}}>{`openssl req -x509 -sha256 -nodes -days 365 -newkey rsa:2048 -keyout myservice.key -out myservice.cert
`}</code></pre>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`完成`}<strong parentName="p">{`配置 Okta 账户`}</strong>{`表单后，单击页面底部的`}<strong parentName="p">{`启用 Okta 认证`}</strong>{`。`}</p>
        <p parentName="li">{`Rancher 会将您重定向到 IdP 登录页面。输入使用 Okta IdP 进行身份验证的凭据，以验证您的 Rancher Okta 配置。`}</p>
        <blockquote parentName="li">
          <p parentName="blockquote"><strong parentName="p">{`注：`}</strong>{` 如果什么都没有发生，很可能是因为您的浏览器阻塞了弹出窗口。请确保您禁用了 Rancher 域的弹出窗口拦截器，并在您可能使用的任何其他扩展中将其列入白名单。`}</p>
        </blockquote>
      </li>
    </ol>
    <p><strong parentName="p">{`结果：`}</strong>{` 配置了使用 Okta 凭证的方式登录 Rancher，您的用户现在可以使用他们的 Okta 账号登录到 Rancher。`}</p>
    <blockquote>
      <p parentName="blockquote">{`SAML 身份验证提供者警告：`}</p>
      <ul parentName="blockquote">
        <li parentName="ul">{`SAML 协议不支持搜索或查找用户或组。因此，将用户或组添加到 Rancher 时不会对其进行验证。`}</li>
        <li parentName="ul">{`添加用户时，必须正确输入确切的用户 ID（即`}<inlineCode parentName="li">{`UID`}</inlineCode>{`字段）。输入用户 ID 时，将不会搜索可能匹配的其他用户 ID。`}</li>
        <li parentName="ul">{`添加组时，必须从文本框旁边的下拉列表中选择组。Rancher 假定来自文本框的任何输入都是用户。`}<ul parentName="li">
            <li parentName="ul">{`用户组下拉列表仅显示您所属的用户组。您将无法添加您不是其成员的组。`}</li>
          </ul></li>
      </ul>
    </blockquote>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
