  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '备份配置',
	description: '通过 Backup Create 页面，您可以配置计划、启用加密和指定备份的存储位置。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
