		'rancher 2.0',
		'rancher2.0 中文教程',
		'备份与恢复',
		'rancher2.5',
		'备份配置'
	]
};
export const metadata = {
  "id": "rancher2/backups/2.5/configuration/back-up-config/_index",
  "title": "备份配置",
  "description": "通过 Backup Create 页面，您可以配置计划、启用加密和指定备份的存储位置。",
  "source": "@site/docs/rancher2/backups/2.5/configuration/back-up-config/_index.md",
