export const rightToc = [{
  value: '定时调度',
  id: '定时调度',
  children: []
}, {
  value: '加密',
  id: '加密',
  children: []
}, {
  value: '存储位置',
  id: '存储位置',
  children: [{
    value: 'S3',
    id: 's3',
    children: []
  }, {
    value: 'S3 存储配置示例',
    id: 's3-存储配置示例',
    children: []
  }, {
    value: 'MinIO 配置示例',
    id: 'minio-配置示例',
    children: []
  }, {
    value: 'CredentialSecret 示例',
    id: 'credentialsecret-示例',
    children: []
  }, {
    value: 'EC2 节点访问 S3 的 IAM 权限设置',
    id: 'ec2-节点访问-s3-的-iam-权限设置',
