  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '介绍',
	description: '安装 Rancher 的方式有两种：单节点安装和高可用集群安装。因为单节点安装只适用于测试和 demo 环境，而且单节点安装和高可用集群安装之间不能进行数据迁移，所以我们推荐从一开始就使用高可用集群安装的方式安装 Rancher。本文将详细介绍高可用集群安装的配置方式。如果您仍然准备在单节点上安装 Rancher，本文只有分开部署 Rancher 与下游集群的部分适用于单节点安装。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'系统工具'
	]
};
export const metadata = {
  "id": "rancher2/system-tools/_index",
  "title": "介绍",
  "description": "安装 Rancher 的方式有两种：单节点安装和高可用集群安装。因为单节点安装只适用于测试和 demo 环境，而且单节点安装和高可用集群安装之间不能进行数据迁移，所以我们推荐从一开始就使用高可用集群安装的方式安装 Rancher。本文将详细介绍高可用集群安装的配置方式。如果您仍然准备在单节点上安装 Rancher，本文只有分开部署 Rancher 与下游集群的部分适用于单节点安装。",
  "source": "@site/docs/rancher2/system-tools/_index.md",
  "permalink": "/docs/rancher2/system-tools/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/system-tools/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
