export const rightToc = [{
  value: '概述',
  id: '概述',
  children: []
}, {
  value: '下载系统工具',
  id: '下载系统工具',
  children: []
}, {
  value: '收集 Kubernetes 日志',
  id: '收集-kubernetes-日志',
  children: [{
    value: '指令介绍',
    id: '指令介绍',
    children: []
  }, {
    value: '使用方法',
    id: '使用方法',
    children: []
  }]
}, {
  value: '查看节点系统指标',
  id: '查看节点系统指标',
  children: [{
    value: '指令介绍',
    id: '指令介绍-1',
    children: []
  }, {
    value: '使用方法',
    id: '使用方法-1',
    children: []
  }]
}, {
  value: '移除 Kubernetes 资源',
  id: '移除-kubernetes-资源',
  children: [{
    value: '指令介绍',
