  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '备份 Rancher',
	description: '在本节中，你将学习如何备份运行在任何 Kubernetes 集群上的 Rancher。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'备份和恢复',
		'rancher 2.5',
		'备份 Rancher'
	]
};
export const metadata = {
  "id": "rancher2/backups/2.5/back-up-rancher/_index",
  "title": "备份 Rancher",
  "description": "在本节中，你将学习如何备份运行在任何 Kubernetes 集群上的 Rancher。",
  "source": "@site/docs/rancher2/backups/2.5/back-up-rancher/_index.md",
