export const rightToc = [{
  value: '先决条件',
  id: '先决条件',
  children: []
}, {
  value: '步骤 1：安装 <code>rancher-backup</code> operator',
  id: '步骤-1：安装-rancher-backup-operator',
  children: []
}, {
  value: '步骤 2：执行备份',
  id: '步骤-2：执行备份',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`在本节中，你将学习如何备份运行在任何 Kubernetes 集群上的 Rancher。要备份通过 Docker 安装的 Rancher，请参考`}<a parentName="p" {...{
        "href": "/docs/rancher2/backups/2.5/docker-installs/docker-backups/_index"
      }}>{`单节点备份`}</a>{`的说明。`}</p>
    <h2 {...{
      "id": "先决条件"
    }}>{`先决条件`}</h2>
    <p>{`Rancher 版本必须是 v2.5.0 及以上。`}</p>
    <h2 {...{
      "id": "步骤-1：安装-rancher-backup-operator"
    }}>{`步骤 1：安装 `}<inlineCode parentName="h2">{`rancher-backup`}</inlineCode>{` operator`}</h2>
    <p>{`备份存储位置是 operator 级别的设置，所以需要在安装或升级 `}<inlineCode parentName="p">{`rancher-backup`}</inlineCode>{` 时进行配置。`}</p>
    <p>{`备份创建为 `}<inlineCode parentName="p">{`.tar.gz`}</inlineCode>{` 文件。这些文件可以推送到 S3 或 Minio，也可以存储在一个持久卷中。`}</p>
    <ol>
      <li parentName="ol">{`在 Rancher UI 中，进入`}<strong parentName="li">{`Cluster Explorer`}</strong>{`。`}</li>
      <li parentName="ol">{`单击 `}<strong parentName="li">{`Apps`}</strong>{`。`}</li>
      <li parentName="ol">{`单击 `}<inlineCode parentName="li">{`rancher-backup`}</inlineCode>{`。`}</li>
      <li parentName="ol">{`配置默认的存储位置。有关帮助，请参阅`}<a parentName="li" {...{
          "href": "/docs/rancher2/backups/2.5/configuration/storage-config/_index"
        }}>{`存储配置部分`}</a>{`。`}</li>
    </ol>
    <h2 {...{
      "id": "步骤-2：执行备份"
    }}>{`步骤 2：执行备份`}</h2>
    <p>{`要执行备份，必须创建 Backup 类型的自定义资源。`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`在`}<strong parentName="p">{`Cluster Explorer`}</strong>{`中，进入左上角的下拉菜单，单击`}<strong parentName="p">{`Rancher Backups`}</strong>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`单击 `}<strong parentName="p">{`Backup`}</strong>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`使用表单或 YAML 编辑器创建 Backup。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`要使用该表单配置 Backup 详细信息，请单击`}<strong parentName="p">{`Create`}</strong>{`，然后参考`}<a parentName="p" {...{
            "href": "./../configuration/back-up-config/_index"
          }}>{`配置参考`}</a>{`和`}<a parentName="p" {...{
            "href": "./../examples/_index#%E5%A4%87%E4%BB%BD"
          }}>{`范例`}</a>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`要使用 YAML 编辑器，我们可以单击`}<strong parentName="p">{`Create > Create from YAML`}</strong>{`，输入 Backup YAML。这个例子备份自定义资源将在 S3 中创建加密的定期备份：`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`apiVersion: resources.cattle.io/v1
kind: Backup
metadata:
  name: s3-recurring-backup
spec:
  storageLocation:
    s3:
      credentialSecretName: s3-creds
      credentialSecretNamespace: default
      bucketName: rancher-backups
      folder: rancher
      region: us-west-2
      endpoint: s3.us-west-2.amazonaws.com
  resourceSetName: rancher-resource-set
  encryptionConfigSecretName: encryptionconfig
  schedule: "@every 1h"
  retentionCount: 10
`}</code></pre>
        <div parentName="li" {...{
          "className": "admonition admonition-note alert alert--secondary"
        }}><div parentName="div" {...{
            "className": "admonition-heading"
          }}><h5 parentName="div"><span parentName="h5" {...{
                "className": "admonition-icon"
              }}><svg parentName="span" {...{
                  "xmlns": "http://www.w3.org/2000/svg",
                  "width": "14",
                  "height": "16",
                  "viewBox": "0 0 14 16"
                }}><path parentName="svg" {...{
                    "fillRule": "evenodd",
                    "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
                  }}></path></svg></span>{`注意`}</h5></div><div parentName="div" {...{
            "className": "admonition-content"
          }}><p parentName="div">{`使用 YAML 编辑器创建备份资源时，`}<inlineCode parentName="p">{`resourceSetName`}</inlineCode>{` 必须设置为 `}<inlineCode parentName="p">{`rancher-resource-set`}</inlineCode>{`。`}</p></div></div>
        <p parentName="li">{`有关配置 Backup 的帮助，请参阅`}<a parentName="p" {...{
            "href": "./../configuration/back-up-config/_index"
          }}>{`配置参考`}</a>{`和`}<a parentName="p" {...{
            "href": "./../examples/_index#%E5%A4%87%E4%BB%BD"
          }}>{`示例`}</a></p>
        <div parentName="li" {...{
          "className": "admonition admonition-important alert alert--info"
        }}><div parentName="div" {...{
            "className": "admonition-heading"
          }}><h5 parentName="div"><span parentName="h5" {...{
                "className": "admonition-icon"
              }}><svg parentName="span" {...{
                  "xmlns": "http://www.w3.org/2000/svg",
                  "width": "14",
                  "height": "16",
                  "viewBox": "0 0 14 16"
                }}><path parentName="svg" {...{
                    "fillRule": "evenodd",
                    "d": "M7 2.3c3.14 0 5.7 2.56 5.7 5.7s-2.56 5.7-5.7 5.7A5.71 5.71 0 0 1 1.3 8c0-3.14 2.56-5.7 5.7-5.7zM7 1C3.14 1 0 4.14 0 8s3.14 7 7 7 7-3.14 7-7-3.14-7-7-7zm1 3H6v5h2V4zm0 6H6v2h2v-2z"
                  }}></path></svg></span>{`重要`}</h5></div><div parentName="div" {...{
            "className": "admonition-content"
          }}><p parentName="div"><inlineCode parentName="p">{`rancher-backup`}</inlineCode>{` operator 不保存 EncryptionConfiguration 文件。创建加密备份时必须保存 EncryptionConfiguration 文件的内容，从该备份中恢复时必须使用相同的文件。`}</p></div></div>
      </li>
      <li parentName="ol">
        <p parentName="li">{`单击 `}<strong parentName="p">{`创建`}</strong></p>
      </li>
    </ol>
    <p><strong parentName="p">{`结果：`}</strong>{`在 Backup 自定义资源中配置的存储位置中创建了备份文件。执行还原时使用该文件的名称。`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
