import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const rightToc = [{
  value: '先决条件',
  id: '先决条件',
  children: [{
    value: '在 vSphere 中创建凭证',
    id: '在-vsphere-中创建凭证',
    children: []
  }, {
    value: '网络权限',
    id: '网络权限',
    children: []
