  children: [{
    value: 'source输入源配置',
    id: 'source输入源配置',
    children: []
  }, {
    value: '消息格式化Kubernetes_metadata--Kubernetes_metadata插件介绍',
    id: '消息格式化kubernetes_metadata--kubernetes_metadata插件介绍',
    children: []
  }, {
    value: '格式化日志流--record_transformer插件介绍',
