  export const frontMatter = {
	title: '版本说明 - v2.3.0',
	description: 'Rancher 2.3.0 版本于 2019 年 10 月 8 日发布。本文介绍了 Rancher 2.3.0 相较上一版本（2.2）的重大改动、新增的实验性功能、已修复的问题和已知问题。',
	keywords: [
		'rancher 2.0中文文档',
