  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Kubernetes 介绍',
	description: 'Rancher v2.x 建立在[Kubernetes](https://kubernetes.io/docs/home/?path=users&persona=app-developer&level=foundational)上的容器调度编排。v2.x 底层技术与 v1.6 有很大的不同，后者支持了几种流行的容器编排工具。由于 Rancher v2.x 现在完全基于 Kubernetes，因此学习 Kubernetes 基础很有帮助。下表介绍并定义了一些关键的 Kubernetes 概念。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'版本迁移',
		'Kubernetes 介绍'
	]
};
export const metadata = {
  "id": "rancher2/v1.6-migration/kub-intro/_index",
  "title": "Kubernetes 介绍",
  "description": "Rancher v2.x 建立在[Kubernetes](https://kubernetes.io/docs/home/?path=users&persona=app-developer&level=foundational)上的容器调度编排。v2.x 底层技术与 v1.6 有很大的不同，后者支持了几种流行的容器编排工具。由于 Rancher v2.x 现在完全基于 Kubernetes，因此学习 Kubernetes 基础很有帮助。下表介绍并定义了一些关键的 Kubernetes 概念。",
  "source": "@site/docs/rancher2/v1.6-migration/kub-intro/_index.md",
  "permalink": "/docs/rancher2/v1.6-migration/kub-intro/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/v1.6-migration/kub-intro/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '迁移备忘录',
  id: '迁移备忘录',
  children: []
}, {
  value: '下一步: 开始使用',
  id: '下一步-开始使用',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Rancher v2.x 建立在`}<a parentName="p" {...{
        "href": "https://kubernetes.io/docs/home/?path=users&persona=app-developer&level=foundational"
      }}>{`Kubernetes`}</a>{`上的容器调度编排。v2.x 底层技术与 v1.6 有很大的不同，后者支持了几种流行的容器编排工具。由于 Rancher v2.x 现在完全基于 Kubernetes，因此学习 Kubernetes 基础很有帮助。`}</p>
    <p>{`下表介绍并定义了一些关键的 Kubernetes 概念。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}><strong parentName="th">{`概念`}</strong></th>
          <th parentName="tr" {...{
            "align": null
          }}><strong parentName="th">{`定义`}</strong></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`集群`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`运行 Kubernetes 管理的容器化应用程序的机器的集合。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`命名空间`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`一个虚拟集群，单个物理集群可以支持多个集群。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`节点`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`组成集群的物理机或虚拟机之一。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Pod`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`最小最简单的 Kubernetes 对象。一个 pod 代表一套正在您的集群上运行的`}<a parentName="td" {...{
              "href": "https://kubernetes.io/docs/concepts/overview/what-is-kubernetes/#why-containers"
            }}>{`容器`}</a>{`。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Deployment`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`管理复制的应用程序的 API 对象。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`工作负载`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`工作负载是为 pods 各项设置部署规则的对象。`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "迁移备忘录"
    }}>{`迁移备忘录`}</h2>
    <p>{`由于 Rancher v1.6 默认为我们的的 Cattle 容器编排器，因此它主要使用与 Cattle 相关的术语。但是，由于 Rancher v2.x 使用 Kubernetes，因此符合 Kubernetes 命名标准。对于不熟悉 Kubernetes 的人来说，这种转变可能会令人困惑，因此我们创建了一个表，该表将 Rancher v1.6 中常用的术语映射到 Rancher v2.x 中的等效术语。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}><strong parentName="th">{`Rancher v1.6`}</strong></th>
          <th parentName="tr" {...{
            "align": null
          }}><strong parentName="th">{`Rancher v2.x`}</strong></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`容器`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Pod`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`服务`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`工作负载`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`负载均衡`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Ingress`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`堆栈`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`命名空间`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`环境`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`项目 (管理) / 集群 (计算)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`主机`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`节点`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`应用商店`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Helm`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`端口映射`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`HostPort (单节点) / NodePort (多节点)`}</td>
        </tr>
      </tbody>
    </table>
    <p>{`有关 Kubernetes 概念的更多详细信息，请参见`}<a parentName="p" {...{
        "href": "https://kubernetes.io/docs/concepts/"
      }}>{`Kubernetes 概念文档`}</a>{`.`}</p>
    <h2 {...{
      "id": "下一步-开始使用"
    }}><a parentName="h2" {...{
        "href": "/docs/rancher2/v1.6-migration/get-started/_index"
      }}>{`下一步: 开始使用`}</a></h2>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
